import React, { useState, useEffect } from 'react';

function App() {
  const [todos, setTodos] = useState([]);

  // Fetch the todos from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:5000/todos') // Make sure the URL matches your Flask API
      .then((response) => response.json())
      .then((data) => setTodos(data));  // Use setTodos to update the state
  }, []);

  // Handle adding a new todo
  const addTodo = (title, description) => {
    fetch('http://localhost:5000/todos', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ title, description }),
    })
      .then((response) => response.json())
      .then((newTodo) => setTodos([...todos, newTodo]));  // Use setTodos to add the new todo
  };

  // Handle deleting a todo
  const deleteTodo = (id) => {
    fetch(`http://localhost:5000/todos/${id}`, {
      method: 'DELETE',
    }).then(() => {
      setTodos(todos.filter((todo) => todo.id !== id)); // Update state after deletion
    });
  };

  return (
    <div>
      <h1>Todo List</h1>

      {/* Render the list of todos */}
      <ul>
        {todos.map((todo) => (
          <li key={todo.id}>
            {todo.title} - {todo.description}
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Button to add a new todo */}
      <button onClick={() => addTodo('New Todo', 'Description of new todo')}>
        Add Todo
      </button>
    </div>
  );
}

export default App;
